mapper.select:
select id, uid, title, start_time,DATE_FORMAT(cy_process_file.start_time,'%Y-%m-%d') startTimeString, update_user,DATE_FORMAT(cy_process_file.update_time,'%Y-%m-%d') updateTimeString, update_time, url, type, page_view, praise, del_flag,description from cy_process_file
title like #{title,jdbcType=VARCHAR}
and type = #{type,jdbcType=CHAR}
and del_flag = 0 order by page_view desc

select id, uid, title, start_time,DATE_FORMAT(cy_process_file.start_time,'%Y-%m-%d') startTimeString, update_user,DATE_FORMAT(cy_process_file.update_time,'%Y-%m-%d') updateTimeString, update_time, url, type, page_view, praise, del_flag,description from cy_process_file
title = #{title,jdbcType=VARCHAR}
and del_flag = 0 order by id desc
